/* Copy Paste Scripts */
function SalinAku(TextToCopy) {
	var TempText = document.createElement("input");
	TempText.value = TextToCopy;
	document.body.appendChild(TempText);
	
	/* Select the text field */
	var isiOSDevice = navigator.userAgent.match(/ipad|iphone/i);
	if (isiOSDevice) {
	  
		var editable = TempText.contentEditable;
		var readOnly = TempText.readOnly;

		TempText.contentEditable = true;
		TempText.readOnly = false;

		var range = document.createRange();
		range.selectNodeContents(TempText);

		var selection = window.getSelection();
		selection.removeAllRanges();
		selection.addRange(range);

		TempText.setSelectionRange(0, 999999);
		TempText.contentEditable = editable;
		TempText.readOnly = readOnly;

	} else {
		TempText.select();
		TempText.setSelectionRange(0, 99999); /* For mobile devices */
	}

	/* Copy the text inside the text field */
	document.execCommand('copy');
	navigator.clipboard.writeText(TempText.value);
	document.body.removeChild(TempText);

	/* Notifikasi */
	alert("Teks disalin: " + TempText.value);
}